.mm {
    &__search {
        &-container {
            position: relative;
            input {
                width: 100%;
                padding: em(13px);
                border-radius: em(24px);
                border: none;
                background-color: $mm-form-input-bg;
                font-family: $font-family-base;
                color: $mm-form-placeholder;
                font-size: em(14px);
                margin: em(18px) 0;
                box-sizing: border-box;
            }
        }

        // Send button
        &-send {
            cursor: pointer;
            position: absolute;
            right: 2em;
            top: 1.8em;
            background: url('./../img/icon-search.svg');
            border: none;
            display: block;
            text-indent: -9999px;
            width: em(30px);
            height: em(30px);
            background-repeat: no-repeat;
            background-position: center center;
        }

        // Filters Dropdowns
        &-select {
            background: url('./../img/icon-select.svg');
            background-repeat: no-repeat;
            background-position: center right 1em;
            -moz-appearance: none;
            -webkit-appearance: none;
            appearance: none;
            font-family: $font-family-base;
            font-size: em(14px);
            box-sizing: border-box;
            padding: em(10px) em(120px) em(10px) em(10px);
            border: 2px solid $mm-form-select-border;
            border-radius: 2px;
            color: #616161;
            line-height: 19px;
        }
    }

    &__results{
        &-grid {
            display: grid;
            column-gap: $grid-gutter-results;
            row-gap: $grid-gutter-results;
            grid-template-columns: repeat(4, 1fr);
            padding: em(21px) 0;
        }

        &-single {
            position: relative;
            border: 2px solid transparent;
            transition: all 0.2s ease-in-out;
            &.selected {
                transition: all 0.2s ease-in-out;
                border-radius: em(5px);
                border: 2px solid $mm-card-selected;
                box-shadow: 0 1px 1px 0 rgba(59,72,77,0.08), 0 1px 3px 1px rgba(59,72,77,0.16);
                &:after {
                    content: "";
                    position: absolute;
                    width: em(64px);
                    height: em(64px);
                    display: inline-block;
                    background: url('./../img/icon-selected.svg');
                    top: -2em;
                    right: -2em;
                }
            }
        }
    }
}

